mapper.select:
SELECT board_num, board_contents, board_date, product_num, username
	FROM comment
WHERE product_num = #{product.productNum}
ORDER BY board_num DESC
LIMIT #{pager.startNum}, #{pager.perPage}

SELECT COUNT(board_num)
	FROM comment
WHERE product_num = #{productNum}

mapper.update:
UPDATE comment
	SET board_contents = #{boardContents}
WHERE board_num = #{boardNum}
 AND username = #{username}

mapper.delete:
DELETE FROM comment
WHERE board_num = #{boardNum}
 AND username = #{username}
